(function($){$.fn.matchHeights=function(minHeight,maxHeight){tallest=(minHeight)?minHeight:0;this.each(function(){if($(this).height()>tallest){tallest=$(this).height()}});if((maxHeight)&&tallest>maxHeight){tallest=maxHeight}return this.each(function(){extra=$(this).outerHeight()-($(this).innerHeight()-parseInt($(this).css("padding-top"))-parseInt($(this).css("padding-bottom")));($.browser.msie&&$.browser.version==6)?$(this).css({height:tallest-extra}):$(this).css({"min-height":tallest-extra})})}})(jQuery);